import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './VideoPage.css';


const VideoPage = () => {
    const [videos, setVideos] = useState([]);
    
    
    
    const [hoveredVideo, setHoveredVideo] = useState(null);
    const [likes, setLikes] = useState({});
    const [dislikes, setDislikes] = useState({});
    

    useEffect(() => {
        const fetchVideos = async () => {
            const apps_url = 'http://localhost:8000/api/videos/';
        fetch(apps_url)
            .then(response => {
                if (!response.ok) {
                    throw new Error('Network response was not ok ' + response.statusText);
                }
                return response.text();
            })
            .then(data => {
                const obj = JSON.parse(data); // or response.json() if returning JSON
                
                for (let i = 0; i < 10; i++) {
                    videos[i].id=obj[0].id;
                    videos[i].title=obj[0].title;
                    videos[i].category=obj[0].category;
                    
                  }
                  setVideos(data);
                  
                //document.getElementById("viewsText").textContent=obj.views;
                //document.getElementById("usersText").textContent=obj.newUsers;
            })
            .catch(error => {
                console.error('There has been a problem with your fetch operation:', error);
            });
        };
        fetchVideos();
    }, []);
    
    const handleLike = (id) => {
        setLikes(prevLikes => ({
            ...prevLikes,
            [id]: !prevLikes[id] // Переключаем лайк
        }));
        setDislikes(prevDislikes => ({
            ...prevDislikes,
            [id]: false // Сбрасываем дизлайк
        }));
    };

    const handleDislike = (id) => {
        setDislikes(prevDislikes => ({
            ...prevDislikes,
            [id]: !prevDislikes[id] // Переключаем дизлайк
        }));
        setLikes(prevLikes => ({
            ...prevLikes,
            [id]: false // Сбрасываем лайк
        }));
    };

   return (
        <div className="video-page">
            <div className="video-list">
                {videos.map((video) => (
                    <div
                        className="video-item"
                        key={video.id}
                        onMouseEnter={() => setHoveredVideo(video)}
                        onMouseLeave={() => setHoveredVideo(null)}
                    >
                        <div className="video-title">{video.title}</div>
                        <div className="video-controls">
                            <button
                                className={`reaction-btn ${likes[video.id] ? 'active' : ''}`}
                                onClick={() => handleLike(video.id)}
                            >
                                •ᴗ•
                            </button>
                            <button
                                className={`reaction-btn ${dislikes[video.id] ? 'noactive' : ''}`}
                                onClick={() => handleDislike(video.id)}
                            >
                                ˙◠˙
                            </button>
                        </div>
                        {hoveredVideo && hoveredVideo.id === video.id && (
                            <div className="tooltip">
                                <div className="video-category">
                                    <strong>Категория:</strong> {video.category}
                                </div>
                                <div className="video-date">
                                    <strong>Дата:</strong> {video.date}
                                </div>
                            </div>
                        )}
                        <div className="current-date">{video.date}</div>
                    </div>
                ))}
            </div>
            <div className="refresh-recommendations">
                <button className="refresh-btn">Обновить рекомендации</button>
            </div>
        </div>
    );


};



export default VideoPage;
