import React, { useState, useEffect } from 'react';
import './FileUploader.css';
import backgroundImage from './train.jpeg';
import backgroundImage2 from './Road.jpg';

const FileUploader = () => {
  const [fileName, setFileName] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const [isFileUploaded, setIsFileUploaded] = useState(false);
  const [isSolutionReady, setIsSolutionReady] = useState(false);
  const [isFilterVisible, setIsFilterVisible] = useState(false);
  const [cityInput, setCityInput] = useState('');
  const [selectedCity, setSelectedCity] = useState('');
  const [cities, setCities] = useState([]);

  // Состояния для фильтров
  const [mspRegistry, setMspRegistry] = useState('');
  const [companySize, setCompanySize] = useState('');
  const [shipper, setShipper] = useState('');
  const [receiver, setReceiver] = useState('');

  useEffect(() => {
    const fetchCities = async () => {
      try {
        const response = await fetch('http://localhost:8000/api/cities/'); // Измените URL на ваш бэкенд
        if (!response.ok) {
          throw new Error('Ошибка загрузки городов');
        }
        const cityList = await response.json();
        setCities(cityList);
      } catch (error) {
        console.error('Ошибка при загрузке городов:', error);
        setErrorMessage('Не удалось загрузить данные городов. Проверьте файл.');
      }
    };

    fetchCities();
  }, []);

  const handleFileChange = (event) => {
    const file = event.target.files[0];

    if (file) {
      const fileName = String(file.name).toLowerCase();
      const isZip = fileName.endsWith('.zip');

      if (isZip) {
        setFileName(file.name);
        setErrorMessage('');
        setIsFileUploaded(true);
        setIsSolutionReady(false);
      } else {
        setFileName('');
        setErrorMessage('Формат файла не подходит. Пожалуйста, загрузите только ZIP файл.');
        setIsFileUploaded(false);
      }
    } else {
      setFileName('');
      setIsFileUploaded(false);
    }
  };

  const handleUploadButtonClick = () => {
    document.getElementById('file-input').click();
  };

  const handleSubmitClick = async () => {
    if (!isFileUploaded) {
      setErrorMessage('Загрузите файл перед отправкой.');
      return;
    }

    // Проверяем, сохранены ли данные фильтра
    if (!mspRegistry && !companySize && !selectedCity && !shipper && !receiver) {
      setErrorMessage('Пожалуйста, сохраните данные в фильтре перед отправкой.');
      return;
    }

    // Задать значения по умолчанию для фильтров, если они не выбраны
    const finalMspRegistry = mspRegistry || ''; // Пустая строка, если не выбрано
    const finalCompanySize = companySize || ''; // Пустая строка, если не выбрано
    const finalCity = selectedCity || ''; // Пустая строка, если не выбрано
    const finalShipper = shipper || ''; // Пустая строка, если не выбрано
    const finalReceiver = receiver || ''; // Пустая строка, если не выбрано

    // Отправка данных на бэкенд
    const formData = new FormData();
    formData.append('file', document.getElementById('file-input').files[0]); // Добавляем файл в formData
    formData.append('msp_registry', finalMspRegistry);
    formData.append('company_size', finalCompanySize);
    formData.append('city', finalCity);
    formData.append('shipper', finalShipper);
    formData.append('receiver', finalReceiver);

    try {
      const response = await fetch('http://localhost:8000/api/upload/', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        throw new Error('Ошибка при отправке данных');
      }

      const result = await response.json();
      console.log('Успех:', result);
      alert('Данные успешно отправлены');
      setIsSolutionReady(true);
    } catch (error) {
      console.error('Ошибка:', error);
      setErrorMessage('Данные в фильтре сохранены, но произошла ошибка при отправке. Пожалуйста, попробуйте еще раз.');
    }
  };

  const toggleFilter = () => {
    setIsFilterVisible(!isFilterVisible);
  };

  const saveFilterSettings = () => {
    // Сохранение значений фильтров
    console.log('Фильтр сохранен', { mspRegistry, companySize, selectedCity, shipper, receiver });
    alert('Значения сохранены!');
  };
  
  // Убедитесь, что в основном рендере отображаете актуальные значения
  return (
    <div className="container">
      {/* Другой код */}
      <p>Находится в реестре МСП: {mspRegistry || 'Не выбрано'}</p>
      <p>Размер компании: {companySize || 'Не выбрано'}</p>
      <p>Город: {selectedCity || 'Не выбран'}</p>
      <p>Грузоотправитель: {shipper || 'Не выбрано'}</p>
      <p>Грузополучатель: {receiver || 'Не выбрано'}</p>
      {/* Другой код */}
    </div>
  );

  const handleCityInputChange = (e) => {
    setCityInput(e.target.value);
  };

  const handleCitySelect = (city) => {
    setSelectedCity(city);
    setCityInput('');
  };

  const filteredCities = cities.filter(city =>
    city.toLowerCase().includes(cityInput.toLowerCase())
  );

  return (
    <div className="container" style={{ backgroundImage: `url(${backgroundImage})` }}>
      <div className="dashed-box">
        <span className="file-prompt-text">{fileName || 'Выберите файл'}</span>
      </div>
      {errorMessage && <div className="tooltip">{errorMessage}</div>}
      <input
        type="file"
        id="file-input"
        accept=".zip"
        onChange={handleFileChange}
        style={{ display: 'none' }}
      />
      <button onClick={handleUploadButtonClick} className="upload-button">
        Загрузить файл
      </button>
      <button onClick={handleSubmitClick} className="submit-button">
        {isFileUploaded
          ? (isSolutionReady ? 'Показать результат' : 'Отправить в ML')
          : 'Вы должны загрузить файл'}
      </button>

      <button className="filter-button" onClick={toggleFilter}>
        ☰
      </button>

      {isFilterVisible && (
        <div className="filter-modal" style={{ backgroundImage: `url(${backgroundImage2})` }}>
          <h2>Фильтр</h2>
          <div className="filter-content">
            <div>
              <h3>Находится в реестре МСП:</h3>
              <select value={mspRegistry} onChange={(e) => setMspRegistry(e.target.value)}>
                <option value="">Выберите...</option>
                <option value="yes">Да</option>
                <option value="no">Нет</option>
              </select>
            </div>
            <div>
              <h3>Размер компании:</h3>
              <select value={companySize} onChange={(e) => setCompanySize(e.target.value)}>
                <option value="">Выберите...</option>
                <option value="micro">Микробизнес</option>
                <option value="small">Малый бизнес</option>
                <option value="medium">Средний бизнес</option>
                <option value="large">Крупный бизнес</option>
              </select>
            </div>
            <div>
              <h3>Город:</h3>
              <div className="city-input-container">
                <input 
                  type="text" 
                  value={cityInput} 
                  placeholder="Поиск..."
                  onChange={handleCityInputChange}
                  onFocus={() => setCityInput('')}
                />
                {cityInput && (
                  <ul className="city-suggestions">
                    {filteredCities.map(city => (
                      <li key={city} onClick={() => handleCitySelect(city)}>
                        {city}
                      </li>
                    ))}
                  </ul>
                )}
              </div>
              <p>Выбранный город: {selectedCity || 'Не выбран'}</p>
            </div>
            <div>
              <h3>Грузоотправитель:</h3>
              <select value={shipper} onChange={(e) => setShipper(e.target.value)}>
                <option value="">Выберите...</option>
                <option value="yes">Да</option>
                <option value="no">Нет</option>
              </select>
            </div>
            <div>
              <h3>Грузополучатель:</h3>
              <select value={receiver} onChange={(e) => setReceiver(e.target.value)}>
                <option value="">Выберите...</option>
                <option value="yes">Да</option>
                <option value="no">Нет</option>
              </select>
            </div>
          </div>
          <button onClick={saveFilterSettings} className="save-button">Сохранить</button>
          <button onClick={toggleFilter} className="close-button">Закрыть</button>
        </div>
      )}
    </div>
  );
};

export default FileUploader;
