import React, { useState } from 'react';
import './FileUploader.css';
import backgroundImage from './train.jpeg';
import backgroundImage2 from './Road.jpg';

const FileUploader = () => {
  const [fileName, setFileName] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const [isFileUploaded, setIsFileUploaded] = useState(false);
  const [isSolutionReady, setIsSolutionReady] = useState(false);
  const [isFilterVisible, setIsFilterVisible] = useState(false);
  const [cityInput, setCityInput] = useState('');
  const [selectedCity, setSelectedCity] = useState('');
  const [cities, setCities] = useState([]);

  // Состояния для фильтров
  const [mspRegistry, setMspRegistry] = useState('');
  const [companySize, setCompanySize] = useState('');
  const [shipper, setShipper] = useState('');
  const [receiver, setReceiver] = useState('');

  // Состояние для результата
  const [resultAvailable, setResultAvailable] = useState(false);
  const [filters, setFilters] = useState({ mspRegistry: '', companySize: '', city: '', shipper: '', receiver: '' });

  const handleFileChange = (event) => {
    const file = event.target.files[0];

    if (file) {
      const fileName = String(file.name).toLowerCase();
      const isZip = fileName.endsWith('.zip');

      if (isZip) {
        setFileName(file.name);
        setErrorMessage('');
        setIsFileUploaded(true);
        setIsSolutionReady(false);
        setResultAvailable(false); // Сбросить состояние кнопки "Показать результат"
      } else {
        setFileName('');
        setErrorMessage('Формат файла не подходит. Пожалуйста, загрузите только ZIP файл.');
        setIsFileUploaded(false);
      }
    } else {
      setFileName('');
      setIsFileUploaded(false);
    }
  };

  const handleUploadButtonClick = () => {
    document.getElementById('file-input').click();
  };

  const handleCheckResultClick = async () => {
    if (!isFileUploaded) {
      setErrorMessage('Загрузите файл перед отправкой.');
      return;
    }

    const formData = new FormData();
    formData.append('file', document.getElementById('file-input').files[0]);

    try {
      const response = await fetch('http://localhost:8000/api/clients/upload-zip/', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        throw new Error('Ошибка при отправке файла для проверки');
      }

      const result = await response.json();
      alert('Данные успешно отправлены');
      setCities(result.cities || []);
      setIsSolutionReady(true);
      setResultAvailable(false);
    } catch (error) {
      setErrorMessage('Ошибка при проверке результата. ');
    } finally {
      setIsSolutionReady(true);
    }
  };

  const handleSubmitClick = async () => {
    if (!mspRegistry && !companySize && !selectedCity && !shipper && !receiver) {
      setErrorMessage('Пожалуйста, выберите хоть что-то в фильтре перед отправкой.');
      return;
    }

    const formData = new FormData();
    formData.append('file', document.getElementById('file-input').files[0]);
    formData.append('msp_registry', filters.mspRegistry);
    formData.append('company_size', filters.companySize);
    formData.append('city', filters.city);
    formData.append('shipper', filters.shipper);
    formData.append('receiver', filters.receiver);

    try {
      const response = await fetch('http://localhost:8000/api/clients/', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        throw new Error('Ошибка при отправке данных');
      }

      alert('Данные успешно отправлены с фильтром');
      setResultAvailable(true);
    } catch (error) {
      setErrorMessage('Ошибка при отправке данных с фильтром.');
    } finally {
      setIsFileUploaded(false);
    }
  };

  const toggleFilter = () => {
    setIsFilterVisible(!isFilterVisible);
  };

  const saveFilterSettings = () => {
    // Убедитесь, что сохраняете выбранные значения
    setFilters({
      mspRegistry: mspRegistry !== '' ? mspRegistry : '0', // сохранять '0', если пусто
      companySize: companySize !== '' ? companySize : '0',
      city: selectedCity !== '' ? selectedCity : '0', // сохранять '0', если пусто
      shipper: shipper !== '' ? shipper : '0',
      receiver: receiver !== '' ? receiver : '0',
    });
    alert('Значения сохранены!');
  };

  const handleCityInputChange = (e) => {
    setCityInput(e.target.value);
  };

  const handleCitySelect = (city) => {
    setSelectedCity(city);
    setCityInput('');
  };

  const filteredCities = cities.filter(city =>
    city.toLowerCase().includes(cityInput.toLowerCase())
  );

  return (
    <div className="container" style={{ backgroundImage: `url(${backgroundImage})` }}>
      <div className="dashed-box">
        <span className="file-prompt-text">{fileName || 'Выберите файл'}</span>
      </div>
      {errorMessage && <div className="tooltip">{errorMessage}</div>}
      <input
        type="file"
        id="file-input"
        accept=".zip"
        onChange={handleFileChange}
        style={{ display: 'none' }}
      />
      <button onClick={handleUploadButtonClick} className="upload-button" disabled={isSolutionReady}>
        Загрузить ZIP файл
      </button>

      <button onClick={isSolutionReady ? handleSubmitClick : handleCheckResultClick} className="submit-button">
        {isSolutionReady ? 'Отправить фильтры' : 'Отправить файл'}
      </button>

      <button 
        className="result-button" 
        onClick={() => console.log('Показать результат')} 
        style={{ 
          backgroundColor: resultAvailable ? '#007BFF' : '#B0B0B0', 
          cursor: resultAvailable ? 'pointer' : 'not-allowed' 
        }}
        disabled={!resultAvailable}
      >
        Показать результат
      </button>

      {isFileUploaded && isSolutionReady && (
        <>
          <button className="filter-button" onClick={toggleFilter}>
            ☰
          </button>

          {isFilterVisible && (
            <div className="filter-modal" style={{ backgroundImage: `url(${backgroundImage2})` }}>
              <h2>Фильтр</h2>
              <div className="filter-content">
                <div>
                  <h3>Находится в реестре МСП:</h3>
                  <select value={mspRegistry} onChange={(e) => setMspRegistry(e.target.value)}>
                    <option value="">Выберите...</option>
                    <option value="yes">Да</option>
                    <option value="no">Нет</option>
                  </select>
                </div>
                <div>
                  <h3>Размер компании:</h3>
                  <select value={companySize} onChange={(e) => setCompanySize(e.target.value)}>
                    <option value="">Выберите...</option>
                    <option value="micro">Микробизнес</option>
                    <option value="small">Малый бизнес</option>
                    <option value="medium">Средний бизнес</option>
                    <option value="large">Крупный бизнес</option>
                  </select>
                </div>
                <div>
                  <h3>Город:</h3>
                  <div className="city-input-container">
                    <input 
                      type="text" 
                      value={cityInput} 
                      placeholder="Поиск..."
                      onChange={handleCityInputChange}
                      onFocus={() => setCityInput('')}
                    />
                    {cityInput && (
                      <ul className="city-suggestions">
                        {filteredCities.map(city => (
                          <li key={city} onClick={() => handleCitySelect(city)}>
                            {city}
                          </li>
                        ))}
                      </ul>
                    )}
                  </div>
                  <p>Выбранный город: {selectedCity || 'Не выбран'}</p>
                </div>
                <div>
                  <h3>Грузоотправитель:</h3>
                  <select value={shipper} onChange={(e) => setShipper(e.target.value)}>
                    <option value="">Выберите...</option>
                    <option value="yes">Да</option>
                    <option value="no">Нет</option>
                  </select>
                </div>
                <div>
                  <h3>Грузополучатель:</h3>
                  <select value={receiver} onChange={(e) => setReceiver(e.target.value)}>
                    <option value="">Выберите...</option>
                    <option value="yes">Да</option>
                    <option value="no">Нет</option>
                  </select>
                </div>
              </div>
              <button onClick={saveFilterSettings} className="save-button">Сохранить</button>
              <button onClick={toggleFilter} className="close-button">Закрыть</button>
              <div className="saved-filters">
        <h4>Сохраненные фильтры:</h4>
        <p>Реестр МСП: {filters.mspRegistry}</p>
        <p>Размер компании: {filters.companySize}</p>
        <p>Город: {filters.city}</p>
        <p>Грузоотправитель: {filters.shipper}</p>
        <p>Грузополучатель: {filters.receiver}</p>
      </div>

              
            </div>
          )}
        </>
      )}
    </div>
  );
};

export default FileUploader;
