import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom'; // Импортируем useNavigate для навигации
import './Result.css';
import logo from './Logo.png';

const Result = () => {
    const navigate = useNavigate(); // Используем useNavigate для навигации
    const [reportVisible, setReportVisible] = useState(false);
    const [results, setResults] = useState([]);

    const handleButtonClick = async () => {
        try {
            const response = await fetch('https://your-api-url.com/report'); // Замените на ваш URL API
            if (!response.ok) {
                throw new Error('Ошибка сети.');
            }
            const data = await response.json();
            setResults(data.results); // Предполагается, что ваш ответ имеет формат { results: [...] }
            setReportVisible(true);
        } catch (error) {
            console.error('Ошибка получения данных:', error);
            // Здесь вы можете обработать ошибку, например, уведомить пользователя
        }
    };

    const handleResetButtonClick = () => {
        // Переход на страницу FileUploader
        navigate('/');
    };

    // Добавляем эффект для сброса состояния при монтировании
    useEffect(() => {
        setReportVisible(false); // Скрываем отчет
        setResults([]); // Очищаем результаты
    }, []);

    return (
        <div className="result-container">
            <div className="header">
                <img src={logo} alt="Логотип" className="logo" />
            </div>
            <button className="rating-button" onClick={handleButtonClick}>
                Сформировать отчет
            </button>

            {reportVisible && (
                <div className="report-container">
                    <div className="report-content">
                        {results.length > 0 ? (
                            results.map(result => (
                                <div key={result.id} className="report-line">
                                    {`ID: ${result.id}, Наименование деятельности: ${result.OKVED2Name}, Расчет: ${result.LivingChance}`}
                                </div>
                            ))
                        ) : (
                            <div className="report-line">Нет данных для отображения.</div>
                        )}
                    </div>
                    <button className="reset-button" onClick={handleResetButtonClick}>
                        Начать сначала
                    </button>
                </div>
            )}
        </div>
    );
};

export default Result;
