import React, { useState, useEffect } from 'react';
import './VideoPage.css';

const VideoPage = () => {
    const [videos, setVideos] = useState([]);
    const [hoveredVideo, setHoveredVideo] = useState(null);
    const [likes, setLikes] = useState({});
    const [dislikes, setDislikes] = useState({});

    const fetchVideos = async () => {
        const apps_url = 'http://localhost:8000/api/videos/';
        try {
            const response = await fetch(apps_url);
            if (!response.ok) {
                throw new Error('Network response was not ok ' + response.statusText);
            }
            const data = await response.json();
            setVideos(data);
        } catch (error) {
            console.error('There has been a problem with your fetch operation:', error);
        }
    };

    useEffect(() => {
        fetchVideos();
    }, []);

    const handleRefresh = async () => {
        const update_url = 'http://localhost:8000/api/videos/update/'; // Adjust API endpoint
        const updates = videos.map(video => ({
            uid: video.uid, // Ensure uid matches your model
            has_liked: likes[video.id] || false,
            has_disliked: dislikes[video.id] || false
        }));

        try {
            const response = await fetch(update_url, {
                method: 'POST', // Use POST method for updating
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(updates), // Convert updates array to JSON for sending
            });

            if (!response.ok) {
                throw new Error('Failed to update videos');
            }

            const result = await response.json();
            console.log(result);
            fetchVideos(); // Refresh video list after successful update
        } catch (error) {
            console.error('Error updating videos:', error);
        }
    };

    const handleLike = (id) => {
        setLikes(prevLikes => ({
            ...prevLikes,
            [id]: !prevLikes[id], // Toggle like
        }));
        setDislikes(prevDislikes => ({
            ...prevDislikes,
            [id]: false, // Reset dislike
        }));
    };

    const handleDislike = (id) => {
        setDislikes(prevDislikes => ({
            ...prevDislikes,
            [id]: !prevDislikes[id], // Toggle dislike
        }));
        setLikes(prevLikes => ({
            ...prevLikes,
            [id]: false, // Reset like
        }));
    };

    return (
        <div className="video-page">
            <div className="video-list">
                {videos.map((video) => (
                    <div
                        className="video-item"
                        key={video.id}
                        onMouseEnter={() => setHoveredVideo(video)}
                        onMouseLeave={() => setHoveredVideo(null)}
                    >
                        <div className="video-title">{video.title}</div>
                        <div className="video-controls">
                            <button
                                className={`reaction-btn ${likes[video.id] ? 'active' : ''}`}
                                onClick={() => handleLike(video.id)}
                            >
                                •ᴗ•
                            </button>
                            <button
                                className={`reaction-btn ${dislikes[video.id] ? 'noactive' : ''}`}
                                onClick={() => handleDislike(video.id)}
                            >
                                ˙◠˙
                            </button>
                        </div>
                        {hoveredVideo && hoveredVideo.id === video.id && (
                            <div className="tooltip">
                                <div className="video-category">
                                    <strong>Категория:</strong> {video.category_name}
                                </div>
                                <div className="video-date">
                                    <strong>Дата:</strong> {video.created_at}
                                </div>
                            </div>
                        )}
                        <div className="current-date">{video.created_at}</div>
                    </div>
                ))}
            </div>
            <div className="refresh-recommendations">
                <button className="refresh-btn" onClick={handleRefresh}>Обновить рекомендации</button>
            </div>
        </div>
    );
};

export default VideoPage;
