import React, { useState } from 'react';

const VideoList = () => {
  // –ú–∞—Å—Å–∏–≤–∞ –≤–∏–¥–µ–æ —Å –Ω–∞–∑–≤–∞–Ω–∏—è–º–∏ –∏ —Ç–∞–π–º-–∫–æ–¥–∞–º–∏
  const videos = Array.from({ length: 10 }, (_, index) => ({
    id: index + 1,
    title: `–ù–∞–∑–≤–∞–Ω–∏–µ –≤–∏–¥–µ–æ ${index + 1}`,
    watchTime: `00:0${index + 1}`
  }));

  // –°–æ—Å—Ç–æ—è–Ω–∏—è –¥–ª—è –ª–∞–π–∫–æ–≤ –∏ –¥–∏–∑–ª–∞–π–∫–æ–≤
  const [likes, setLikes] = useState(Array(10).fill(false));
  const [dislikes, setDislikes] = useState(Array(10).fill(false));

  // –û–±—Ä–∞–±–æ—Ç—á–∏–∫ –ª–∞–π–∫–∞
  const toggleLike = (id) => {
    setLikes((prevLikes) => {
      const newLikes = [...prevLikes];
      newLikes[id - 1] = !newLikes[id - 1];
      if (newLikes[id - 1]) {
        setDislikes((prev) => {
          const newDislikes = [...prev];
          newDislikes[id - 1] = false; // –£–±–∏—Ä–∞–µ–º –¥–∏–∑–ª–∞–π–∫
          return newDislikes;
        });
      }
      return newLikes;
    });
  };

  // –û–±—Ä–∞–±–æ—Ç—á–∏–∫ –¥–∏–∑–ª–∞–π–∫–∞
  const toggleDislike = (id) => {
    setDislikes((prevDislikes) => {
      const newDislikes = [...prevDislikes];
      newDislikes[id - 1] = !newDislikes[id - 1];
      if (newDislikes[id - 1]) {
        setLikes((prev) => {
          const newLikes = [...prev];
          newLikes[id - 1] = false; // –£–±–∏—Ä–∞–µ–º –ª–∞–π–∫
          return newLikes;
        });
      }
      return newDislikes;
    });
  };

  return (
    <div className="video-page">
      <div className="video-list">
        {videos.map((video) => (
          <div className="video-item" key={video.id} id={`video${video.id}`}>
            <div className="video-title">{video.title}</div>
            <div className="video-controls">
              <button
                className={`reaction-btn ${likes[video.id - 1] ? 'active' : ''}`}
                onClick={() => toggleLike(video.id)}
              >
                üëç
              </button>
              <button
                className={`reaction-btn ${dislikes[video.id - 1] ? 'active' : ''}`}
                onClick={() => toggleDislike(video.id)}
              >
                üëé
              </button>
            </div>
            <div className="watch-time">{video.watchTime}</div>
            <div className="tooltip" id={`tooltip${video.id}`}>
              –ò–Ω—Ñ–æ—Ä–º–∞—Ü–∏—è –æ {video.title}
            </div>
          </div>
        ))}
      </div>
      <div className="refresh-recommendations">
        <button className="refresh-btn">–û–±–Ω–æ–≤–∏—Ç—å —Ä–µ–∫–æ–º–µ–Ω–¥–∞—Ü–∏–∏</button>
      </div>
    </div>
  );
};

export default VideoList;
