import React, { useState, useEffect } from 'react';
import './FileUploader.css';
import backgroundImage from './train.jpeg';
import backgroundImage2 from './Road.jpg';

const FileUploader = () => {
  const [fileName, setFileName] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const [isFileUploaded, setIsFileUploaded] = useState(false);
  const [isSolutionReady, setIsSolutionReady] = useState(false);
  const [isFilterVisible, setIsFilterVisible] = useState(false);
  const [cityInput, setCityInput] = useState('');
  const [selectedCity, setSelectedCity] = useState('');
  const [cities, setCities] = useState([]);

  // Fetch cities from your API
  useEffect(() => {
    const fetchCities = async () => {
      try {
        const response = await fetch('http://localhost:8000/api/cities/'); // Измените URL на ваш бэкенд
        if (!response.ok) {
          throw new Error('Ошибка загрузки городов');
        }
        const cityList = await response.json();
        setCities(cityList);
      } catch (error) {
        console.error('Ошибка при загрузке городов:', error);
        setErrorMessage('Не удалось загрузить данные городов. Проверьте файл.');
      }
    };

    fetchCities();
  }, []);

  const handleFileChange = (event) => {
    const file = event.target.files[0];
  
    if (file) {
      const fileName = String(file.name).toLowerCase();
      const isZip = fileName.endsWith('.zip');
  
      if (isZip) {
        setFileName(file.name);
        setErrorMessage('');
        setIsFileUploaded(true);
        setIsSolutionReady(false);
      } else {
        setFileName('');
        setErrorMessage('Формат файла не подходит. Пожалуйста, загрузите только ZIP файл.');
        setIsFileUploaded(false);
      }
    } else {
      setFileName('');
      setIsFileUploaded(false);
    }
  };

  const handleUploadButtonClick = () => {
    document.getElementById('file-input').click();
  };

  const handleSubmitClick = () => {
    if (!isFileUploaded) {
      setErrorMessage('Загрузите файл перед отправкой.');
    } else if (!isSolutionReady) {
      // Добавляем подтверждение
      const confirmUpdate = window.confirm('Вы точно хотите обновить файл?');
      if (confirmUpdate) {
        console.log('Отправка файла в ML...');
        setIsSolutionReady(true);
      }
    } else {
      console.log('Показать результат...');
    }
  };

  const toggleFilter = () => {
    setIsFilterVisible(!isFilterVisible);
  };

  const saveFilterSettings = () => {
    console.log('Фильтр сохранен');
    alert('Значения сохранены!');
  };

  const handleCityInputChange = (e) => {
    setCityInput(e.target.value);
  };

  const handleCitySelect = (city) => {
    setSelectedCity(city);
    setCityInput('');
  };

  const filteredCities = cities.filter(city =>
    city.toLowerCase().includes(cityInput.toLowerCase())
  );

  return (
    <div className="container" style={{ backgroundImage: `url(${backgroundImage})` }}>
      <div className="dashed-box">
        <span className="file-prompt-text">{fileName || 'Выберите файл'}</span>
      </div>
      {errorMessage && <div className="tooltip">{errorMessage}</div>}
      <input
        type="file"
        id="file-input"
        accept=".zip"
        onChange={handleFileChange}
        style={{ display: 'none' }}
      />
      <button onClick={handleUploadButtonClick} className="upload-button">
        Загрузить файл
      </button>
      <button onClick={handleSubmitClick} className="submit-button">
        {isFileUploaded
          ? (isSolutionReady ? 'Показать результат' : 'Отправить в ML')
          : 'Вы должны загрузить файл'}
      </button>

      <button className="filter-button" onClick={toggleFilter}>
        ☰
      </button>
      
      {isFilterVisible && (
        <div className="filter-modal" style={{ backgroundImage: `url(${backgroundImage2})` }}>
          <h2>Фильтр</h2>
          <div className="filter-content">
            <div>
              <h3>Находится в реестре МСП:</h3>
              <select>
                <option value="yes">Да</option>
                <option value="no">Нет</option>
              </select>
            </div>
            <div>
              <h3>Размер компании:</h3>
              <select>
                <option value="micro">Микробизнес</option>
                <option value="small">Малый бизнес</option>
                <option value="medium">Средний бизнес</option>
                <option value="large">Крупный бизнес</option>
              </select>
            </div>
            <div>
              <h3>Город:</h3>
              <div className="city-input-container">
                <input 
                  type="text" 
                  value={cityInput} 
                  placeholder="Поиск..."
                  onChange={handleCityInputChange}
                  onFocus={() => setCityInput('')}
                />
                {cityInput && (
                  <ul className="city-suggestions">
                    {filteredCities.map(city => (
                      <li key={city} onClick={() => handleCitySelect(city)}>
                        {city}
                      </li>
                    ))}
                  </ul>
                )}
              </div>
              <p>Выбранный город: {selectedCity || 'Не выбран'}</p>
            </div>
            <div>
              <h3>Грузоотправитель:</h3>
              <select>
                <option value="yes">Да</option>
                <option value="no">Нет</option>
              </select>
            </div>
            <div>
              <h3>Грузополучатель:</h3>
              <select>
                <option value="yes">Да</option>
                <option value="no">Нет</option>
              </select>
            </div>
          </div>
          <button onClick={saveFilterSettings} className="save-button">Сохранить</button>
          <button onClick={toggleFilter} className="close-button">Закрыть</button>
        </div>
      )}
    </div>
  );
};

export default FileUploader;
