import React, { useState, useEffect } from 'react';
import './VideoPage.css';
import logo from './logo.jpg';

const VideoPage = () => {
    const [loading, setLoading] = useState(true);
    const [videos, setVideos] = useState([]);
    const [hoveredVideo, setHoveredVideo] = useState(null);
    const [likes, setLikes] = useState({});
    const [dislikes, setDislikes] = useState({});
    const [useVideoId, setUseVideoId] = useState(false); // Состояние для переключения

    // Обработка события прокрутки
    useEffect(() => {
        const handleWheel = (event) => {
            if (event.ctrlKey) {
                event.preventDefault(); // Отменяем поведение по умолчанию для прокрутки с Ctrl
            }
        };

        window.addEventListener('wheel', handleWheel, { passive: false });

        return () => {
            window.removeEventListener('wheel', handleWheel);
        };
    }, []);

    // Функция получения видео с сервера
    const fetchVideos = async (retryCount = 0) => {
        const apps_url = 'http://localhost:8000/api/videos/';
        setLoading(true);
        try {
            const response = await fetch(apps_url);
            if (!response.ok) {
                throw new Error('Network response was not ok ' + response.statusText);
            }
            const data = await response.json();
            setVideos(data);
        } catch (error) {
            if (retryCount < 3) {
                setTimeout(() => {
                    fetchVideos(retryCount + 1);
                }, 2000);
            } else {
                console.error('There has been a problem with your fetch operation:', error);
            }
        } finally {
            setLoading(false);
        }
    };

    // Используем useEffect для загрузки видео один раз при загрузке компонента
    useEffect(() => {
        fetchVideos();
    }, []);

    // Функция для обновления лайков и дизлайков на сервере и получения обновленных видео
    const handleRefresh = async () => {
        const update_url = 'http://localhost:8000/videos/update_likes_dislikes/';

        const updates = videos.map(video => ({
            uid: video.uid, 
            has_liked: likes[useVideoId ? video.video_id : video.uid] || false,
            has_disliked: dislikes[useVideoId ? video.video_id : video.uid] || false
        }));

        console.log('Updates to send:', updates);

        try {
            const response = await fetch(update_url, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(updates),
            });

            if (!response.ok) {
                throw new Error('Failed to update videos');
            }

            const result = await response.json();
            console.log('Response from server:', result); // Новый результат от сервера

            // Устанавливаем состояние useVideoId на true после обновления
            setUseVideoId(true);

            // Запрашиваем свежие данные о видео после обновления
            await fetchVideos(); // Получаем актуальные данные о видео
        } catch (error) {
            console.error('Error updating videos:', error);
        }
    };

    // Функция для обработки лайка видео
    const handleLike = (uid) => {
        setLikes(prevLikes => ({
            ...prevLikes,
            [useVideoId ? uid.video_id : uid]: !prevLikes[useVideoId ? uid.video_id : uid], // Переключаем лайк по uid или video_id
        }));
        setDislikes(prevDislikes => ({
            ...prevDislikes,
            [useVideoId ? uid.video_id : uid]: false, // Сбрасываем дизлайк
        }));
    };

    // Функция для обработки дизлайка видео
    const handleDislike = (uid) => {
        setDislikes(prevDislikes => ({
            ...prevDislikes,
            [useVideoId ? uid.video_id : uid]: !prevDislikes[useVideoId ? uid.video_id : uid], // Переключаем дизлайк по uid или video_id
        }));
        setLikes(prevLikes => ({
            ...prevLikes,
            [useVideoId ? uid.video_id : uid]: false, // Сбрасываем лайк
        }));
    };

    return (
        <div className="video-page">
            <img src={logo} alt="Логотип" className="logo" />
            {loading ? (
                <div className="loading-indicator">Загрузка данных...</div>
            ) : (
                <div className="video-list">
                    {videos.map((video) => (
                        <div
                            className="video-item"
                            key={useVideoId ? video.video_id : video.uid}
                            onMouseEnter={() => setHoveredVideo(video)}
                            onMouseLeave={() => setHoveredVideo(null)}
                        >
                            <div className="video-title">{video.title}</div>
                            <div className="video-controls">
                                <button
                                    className={`reaction-btn ${likes[useVideoId ? video.video_id : video.uid] ? 'active' : ''}`}
                                    onClick={() => handleLike(video)}
                                >
                                    •ᴗ•
                                </button>
                                <button
                                    className={`reaction-btn ${dislikes[useVideoId ? video.video_id : video.uid] ? 'noactive' : ''}`}
                                    onClick={() => handleDislike(video)}
                                >
                                    ˙◠˙
                                </button>
                            </div>
                            {hoveredVideo && hoveredVideo.uid === video.uid && (
                                <div className="tooltip">
                                    <div className="video-category">
                                        <strong>Категория:</strong> {video.category_name}
                                    </div>
                                    <div className="video-date">
                                        <strong>Дата:</strong> {video.created_at}
                                    </div>
                                </div>
                            )}
                            <div className="current-date">{video.created_at}</div>
                        </div>
                    ))}
                </div>
            )}
            <div className="refresh-recommendations">
                <button className="refresh-btn" onClick={handleRefresh}>Обновить рекомендации</button>
            </div>
        </div>
    );
};

export default VideoPage;
