import React, { useState } from 'react';
import './VideoPage.css'; // –ò–º–ø–æ—Ä—Ç–∏—Ä—É–µ–º —Å—Ç–∏–ª–∏

const VideoPage = () => {
    const videos = [
        { title: '–í–∏–¥–µ–æ 1', description: '–û–ø–∏—Å–∞–Ω–∏–µ –≤–∏–¥–µ–æ 1', category: '–ö–∞—Ç–µ–≥–æ—Ä–∏—è 1' },
        { title: '–í–∏–¥–µ–æ 2', description: '–û–ø–∏—Å–∞–Ω–∏–µ –≤–∏–¥–µ–æ 2', category: '–ö–∞—Ç–µ–≥–æ—Ä–∏—è 2' },
        { title: '–í–∏–¥–µ–æ 3', description: '–û–ø–∏—Å–∞–Ω–∏–µ –≤–∏–¥–µ–æ 3', category: '–ö–∞—Ç–µ–≥–æ—Ä–∏—è 3' },
        // ... –î—Ä—É–≥–∏–µ –≤–∏–¥–µ–æ
    ];

    const [hoveredVideo, setHoveredVideo] = useState(null);
    const [likes, setLikes] = useState(Array(videos.length).fill(false));
    const [dislikes, setDislikes] = useState(Array(videos.length).fill(false));

    const handleLike = (index) => {
        const newLikes = [...likes];
        const newDislikes = [...dislikes];
        newLikes[index] = !newLikes[index]; // –ü–µ—Ä–µ–∫–ª—é—á–∞–µ–º –ª–∞–π–∫
        newDislikes[index] = false; // –°–±—Ä–∞—Å—ã–≤–∞–µ–º –¥–∏–∑–ª–∞–π–∫
        setLikes(newLikes);
        setDislikes(newDislikes);
    };

    const handleDislike = (index) => {
        const newLikes = [...likes];
        const newDislikes = [...dislikes];
        newDislikes[index] = !newDislikes[index]; // –ü–µ—Ä–µ–∫–ª—é—á–∞–µ–º –¥–∏–∑–ª–∞–π–∫
        newLikes[index] = false; // –°–±—Ä–∞—Å—ã–≤–∞–µ–º –ª–∞–π–∫
        setLikes(newLikes);
        setDislikes(newDislikes);
    };

    return (
        <div className="video-page">
            <div className="video-list">
                {videos.map((video, index) => (
                    <div
                        className="video-item"
                        key={video.title}
                        onMouseEnter={() => setHoveredVideo(video)}
                        onMouseLeave={() => setHoveredVideo(null)}
                    >
                        <div className="video-title">{video.title}</div>
                        <div className="video-controls">
                            <button
                                className={`like-btn ${likes[index] ? 'active' : ''}`}
                                onClick={() => handleLike(index)}
                            >
                                üëç
                            </button>
                            <button
                                className={`dislike-btn ${dislikes[index] ? 'active' : ''}`}
                                onClick={() => handleDislike(index)}
                            >
                                üëé
                            </button>
                            <div className="watch-time">00:30</div>
                        </div>
                    </div>
                ))}
            </div>
            <div className="refresh-recommendations">
                <button className="refresh-btn">–û–±–Ω–æ–≤–∏—Ç—å —Ä–µ–∫–æ–º–µ–Ω–¥–∞—Ü–∏–∏</button>
            </div>
            {hoveredVideo && (
                <div className="tooltip">
                    <div className="video-description">
                        <strong>–û–ø–∏—Å–∞–Ω–∏–µ:</strong> {hoveredVideo.description}
                    </div>
                    <div className="video-category">
                        <strong>–ö–∞—Ç–µ–≥–æ—Ä–∏—è:</strong> {hoveredVideo.category}
                    </div>
                </div>
            )}
        </div>
    );
};

export default VideoPage;
