import React, { useState } from 'react';
import api, { setAccessToken } from '../api';
import './AuthModal.css';

const AuthModal = ({ isLogin, onClose, onSwitchMode }) => {
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: '',
    confirmPassword: ''
  });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
    setError('');
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      if (isLogin) {
        // Логин
        const res = await fetch("http://localhost:8000/api/auth/token/", {
          method: "POST",
          credentials: "include",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({
            username: formData.username,
            password: formData.password
          }),
        });
        
        const data = await res.json();
        if (res.ok) {
          setAccessToken(data.access);
          onClose();
          window.location.reload();
        } else {
          setError(data.detail || "Ошибка входа");
        }
      } else {
        // Регистрация
        if (formData.password !== formData.confirmPassword) {
          setError("Пароли не совпадают");
          setLoading(false);
          return;
        }

        const res = await api.post("/auth/register/", {
          username: formData.username,
          email: formData.email,
          password: formData.password
        });

        if (res.status === 201) {
          // После успешной регистрации автоматически логинимся
          const loginRes = await fetch("http://localhost:8000/api/auth/token/", {
            method: "POST",
            credentials: "include",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify({
              username: formData.username,
              password: formData.password
            }),
          });
          
          const loginData = await loginRes.json();
          if (loginRes.ok) {
            setAccessToken(loginData.access);
            onClose();
            window.location.reload();
          }
        }
      }
    } catch (err) {
      setError(err.response?.data?.detail || "Произошла ошибка");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="modal-content" onClick={(e) => e.stopPropagation()}>
        <button className="close-btn" onClick={onClose}>×</button>
        
        <h2>{isLogin ? 'ВОЙТИ' : 'РЕГИСТРАЦИЯ'}</h2>
        
        {error && <div className="error-message">{error}</div>}
        
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <input
              type="text"
              name="username"
              placeholder="Имя пользователя"
              value={formData.username}
              onChange={handleChange}
              required
            />
          </div>
          
          {!isLogin && (
            <div className="form-group">
              <input
                type="email"
                name="email"
                placeholder="Email"
                value={formData.email}
                onChange={handleChange}
                required
              />
            </div>
          )}
          
          <div className="form-group">
            <input
              type="password"
              name="password"
              placeholder="Пароль"
              value={formData.password}
              onChange={handleChange}
              required
            />
          </div>
          
          {!isLogin && (
            <div className="form-group">
              <input
                type="password"
                name="confirmPassword"
                placeholder="Подтвердите пароль"
                value={formData.confirmPassword}
                onChange={handleChange}
                required
              />
            </div>
          )}
          
          <button type="submit" disabled={loading} className="submit-btn">
            {loading ? 'Загрузка...' : (isLogin ? 'Войти' : 'Зарегистрироваться')}
          </button>
        </form>
        
        <div className="switch-mode">
          <span>
            {isLogin ? 'Нет аккаунта? ' : 'Уже есть аккаунт? '}
            <button type="button" onClick={onSwitchMode} className="switch-btn">
              {isLogin ? 'Зарегистрироваться' : 'Войти'}
            </button>
          </span>
        </div>
      </div>
    </div>
  );
};

export default AuthModal;
