import React, { useState } from 'react';
import './FileUploader.css'; // Импортируйте ваш CSS файл

const FileUploader = () => {
  const [fileName, setFileName] = useState('');
  const [backgroundImage, setBackgroundImage] = useState('C:/Users/Ideapad/PVD_HACKATON/Frontend/src/background.jpg'); // Замените на путь к вашему фото

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    if (file) {
      setFileName(file.name);
    }
  };

  return (
    <div className="container" style={{ backgroundImage: backgroundImage }}>
      <div className="dashed-box">
        {fileName || 'Выберите файл'}
      </div>
      <input
        type="file"
        id="file-input"
        onChange={handleFileChange}
        style={{ display: 'none' }}
      />
      <label htmlFor="file-input">
        <button className="upload-button">
          Загрузить файл
        </button>
      </label>
    </div>
  );
};

export default FileUploader;
