import React, { useState, useEffect } from 'react';
import './FileUploader.css';
import backgroundImage from './train.jpeg';

const FileUploader = () => {
  const [fileName, setFileName] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const [isFileUploaded, setIsFileUploaded] = useState(false);
  const [isSolutionReady, setIsSolutionReady] = useState(false);

  useEffect(() => {
    const handleKeyDown = (event) => {
      // Запретить "Ctrl + Z"
      if (event.ctrlKey && event.key === 'z') {
        event.preventDefault();
      }
    };

    const handleWheel = (event) => {
      // Запретить прокрутку колесиком мыши
      event.preventDefault();
    };

    window.addEventListener('keydown', handleKeyDown);
    window.addEventListener('wheel', handleWheel, { passive: false });

    return () => {
      window.removeEventListener('keydown', handleKeyDown);
      window.removeEventListener('wheel', handleWheel);
    };
  }, []);

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    const validFormats = ['application/zip', 'application/x-rar-compressed'];
    
    if (file) {
      const is7z = file.name.toLowerCase().endsWith('.7z');
      const isRAR = file.name.toLowerCase().endsWith('.rar');
      const isZip = file.name.toLowerCase().endsWith('.zip'); // Проверка на .zip
  
      // Проверка на допустимые форматы
      if (validFormats.includes(file.type) || is7z || isRAR || isZip) {
        setFileName(file.name);
        setErrorMessage(''); // Очищаем сообщение об ошибке
        setIsFileUploaded(true);
        setIsSolutionReady(false); // Сбрасываем состояние решения
      } else {
        setFileName('');
        setErrorMessage('Формат файла не подходит. Пожалуйста, загрузите ZIP, 7z или RAR архив.');
        setIsFileUploaded(false);
      }
    } else {
      setFileName(''); // Очищаем имя файла, если ничего не выбрано
      setIsFileUploaded(false);
    }
  };
  
  
  const handleUploadButtonClick = () => {
    if (isSolutionReady) {
      const confirmReset = window.confirm('Вы уверены, что хотите сбросить этот результат?');
      if (confirmReset) {
        // Сбрасываем состояние
        setFileName('');
        setErrorMessage('');
        setIsFileUploaded(false);
        setIsSolutionReady(false);
        document.getElementById('file-input').click(); // Открываем окно загрузки файла
      }
    } else {
      document.getElementById('file-input').click(); // Открываем окно загрузки файла
    }
  };

  const handleSubmitClick = () => {
    if (!isFileUploaded) {
      setErrorMessage('Загрузите файл перед отправкой.');
    } else if (!isSolutionReady) {
      console.log('Отправка файла в ML...'); // Замените на реальную реализацию
      setIsSolutionReady(true); // Устанавливаем состояние, что решение готово
    } else {
      console.log('Показать результат...'); // Замените на реальную реализацию
    }
  };

  return (
    <div className="container" style={{ backgroundImage: `url(${backgroundImage})` }}>
      <div className="dashed-box">
        <span className="file-prompt-text">{fileName || 'Выберите файл, принимается только ZIP, 7z или RAR'}</span>
      </div>
      {errorMessage && <div className="tooltip">{errorMessage}</div>}
      <input
        type="file"
        id="file-input"
        accept=".zip,.rar,.7z" // Разрешаем только ZIP, RAR, и 7z файлы
        onChange={handleFileChange}
        style={{ display: 'none' }} // Скрываем стандартный input
      />
      <button 
        onClick={handleUploadButtonClick}
        className="upload-button"
      >
        Загрузить файл
      </button>
      <button 
        onClick={handleSubmitClick} 
        className="submit-button"
      >
        {isFileUploaded 
          ? (isSolutionReady ? 'Показать результат' : 'Отправить в ML') 
          : 'Вы должны загрузить файл'}
      </button>
    </div>
  );
};

export default FileUploader;
