import React, { useState } from 'react';
import './FileUploader.css';
import backgroundImage from './train.jpeg';

const FileUploader = () => {
  const [fileName, setFileName] = useState('');
  const [errorMessage, setErrorMessage] = useState('');
  const [isFileUploaded, setIsFileUploaded] = useState(false);
  const [isSolutionReady, setIsSolutionReady] = useState(false);

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    const validFormats = [
      'application/pdf',
      'application/vnd.openxmlformats-officedocument.wordprocessingml.document',
      'application/vnd.openxmlformats-officedocument.spreadsheetml.sheet',
      'application/x-7z-compressed', // 7zip
      'application/vnd.rar', // RAR
    ];

    if (file) {
      if (validFormats.includes(file.type)) {
        setFileName(file.name);
        setErrorMessage(''); // Очищаем сообщение об ошибке
        setIsFileUploaded(true);
        setIsSolutionReady(false); // Сбрасываем состояние решения
      } else {
        setFileName('');
        setErrorMessage('Формат файла не подходит. Попробуйте еще раз.');
        setIsFileUploaded(false);
      }
    } else {
      setFileName(''); // Очищаем имя файла, если ничего не выбрано
      setIsFileUploaded(false);
    }
  };

  const handleSubmitClick = () => {
    if (!isFileUploaded) {
      setErrorMessage('Загрузите файл перед отправкой.');
    } else if (!isSolutionReady) {
      // Симуляция отправки файла на ML
      console.log('Отправка файла в ML...'); // Замените на реальную реализацию
      setIsSolutionReady(true);
    } else {
      console.log('Показать результат...'); // Замените на реальную реализацию
    }
  };

  const handleNewFileUpload = () => {
    setFileName(''); // Очищаем имя файла
    setErrorMessage(''); // Очищаем сообщение об ошибке
    setIsFileUploaded(false); // Файл не загружен
    setIsSolutionReady(false); // Сбрасываем состояние
  };

  return (
    <div className="container" style={{ backgroundImage: `url(${backgroundImage})` }}>
      <div className="dashed-box">
        {fileName || 'Выберите файл'}
      </div>
      {errorMessage && <div className="tooltip">{errorMessage}</div>}
      <input
        type="file"
        id="file-input"
        onChange={(event) => {
          handleFileChange(event);
          handleNewFileUpload(); // Обновляем состояние при новой загрузке
        }}
        style={{ display: 'none' }} // Скрываем стандартный input
      />
      <button 
        onClick={() => document.getElementById('file-input').click()} 
        className="upload-button"
      >
        Загрузить файл
      </button>
      <button 
        onClick={handleSubmitClick} 
        className="submit-button"
      >
        {isFileUploaded && !isSolutionReady ? 'Показать результат' : 'Отправить в ML'}
      </button>
    </div>
  );
};

export default FileUploader;
