import React, { useState } from 'react';
import './VideoPage.css';

const VideoPage = () => {
    const videos = [
        { title: '–í–∏–¥–µ–æ 1', description: '–û–ø–∏—Å–∞–Ω–∏–µ –≤–∏–¥–µ–æ 1', category: '–ö–∞—Ç–µ–≥–æ—Ä–∏—è 1' },
        { title: '–í–∏–¥–µ–æ 2', description: '–û–ø–∏—Å–∞–Ω–∏–µ –≤–∏–¥–µ–æ 2', category: '–ö–∞—Ç–µ–≥–æ—Ä–∏—è 2' },
        // ... –î—Ä—É–≥–∏–µ –≤–∏–¥–µ–æ
    ];

    const [hoveredVideo, setHoveredVideo] = useState(null);
    const [likes, setLikes] = useState({}); // –•—Ä–∞–Ω–µ–Ω–∏–µ —Å–æ—Å—Ç–æ—è–Ω–∏—è –ª–∞–π–∫–æ–≤
    const [dislikes, setDislikes] = useState({}); // –•—Ä–∞–Ω–µ–Ω–∏–µ —Å–æ—Å—Ç–æ—è–Ω–∏—è –¥–∏–∑–ª–∞–π–∫–æ–≤

    const handleLikeToggle = (title) => {
        setLikes((prev) => ({
            ...prev,
            [title]: !prev[title],
        }));
        if (dislikes[title]) {
            setDislikes((prev) => ({ ...prev, [title]: false })); // –°–Ω–∏–º–∞–µ–º –¥–∏–∑–ª–∞–π–∫, –µ—Å–ª–∏ –æ–Ω —É—Å—Ç–∞–Ω–æ–≤–ª–µ–Ω
        }
    };

    const handleDislikeToggle = (title) => {
        setDislikes((prev) => ({
            ...prev,
            [title]: !prev[title],
        }));
        if (likes[title]) {
            setLikes((prev) => ({ ...prev, [title]: false })); // –°–Ω–∏–º–∞–µ–º –ª–∞–π–∫, –µ—Å–ª–∏ –æ–Ω —É—Å—Ç–∞–Ω–æ–≤–ª–µ–Ω
        }
    };

    return (
        <div className="video-page">
            <div className="video-list">
                {videos.map((video) => (
                    <div
                        className="video-item"
                        key={video.title}
                        onMouseEnter={() => setHoveredVideo(video)}
                        onMouseLeave={() => setHoveredVideo(null)}
                    >
                        <div className="video-title">{video.title}</div>
                        <div className="video-controls">
                            <button
                                className={`like-btn ${likes[video.title] ? 'active' : ''}`}
                                onClick={() => handleLikeToggle(video.title)}
                                aria-label="Like"
                            >
                                üëç
                            </button>
                            <button
                                className={`dislike-btn ${dislikes[video.title] ? 'active' : ''}`}
                                onClick={() => handleDislikeToggle(video.title)}
                                aria-label="Dislike"
                            >
                                üëé
                            </button>
                        </div>
                        {hoveredVideo && hoveredVideo.title === video.title && (
                            <div className="tooltip">
                                <div className="video-description">
                                    <strong>–û–ø–∏—Å–∞–Ω–∏–µ:</strong> {video.description}
                                </div>
                                <div className="video-category">
                                    <strong>–ö–∞—Ç–µ–≥–æ—Ä–∏—è:</strong> {video.category}
                                </div>
                            </div>
                        )}
                    </div>
                ))}
            </div>
            <div className="refresh-recommendations">
                <button className="refresh-btn">–û–±–Ω–æ–≤–∏—Ç—å —Ä–µ–∫–æ–º–µ–Ω–¥–∞—ÜÔøΩÔøΩ–∏</button>
            </div>
        </div>
    );
};

export default VideoPage;
