import React, { useState, useEffect } from 'react';
import './VideoPage.css';

const VideoPage = () => {
    const [videos, setVideos] = useState([]);
    const [hoveredVideo, setHoveredVideo] = useState(null);
    const [likes, setLikes] = useState({});
    const [dislikes, setDislikes] = useState({});

    // Функция для получения видео
    const fetchVideos = async () => {
        const apps_url = 'http://localhost:8000/api/videos/';
        try {
            const response = await fetch(apps_url);
            if (!response.ok) {
                throw new Error('Network response was not ok ' + response.statusText);
            }
            const data = await response.json();
            setVideos(data);
        } catch (error) {
            console.error('There has been a problem with your fetch operation:', error);
        }
    };

    useEffect(() => {
        fetchVideos();
    }, []);

    // Функция для обработки обновления лайков/дизлайков
    const handleRefresh = async () => {
        const update_url = 'http://localhost:8000/videos/update_likes_dislikes/';
        const updates = videos.map(video => ({
            uid: video.uid, // Используем uid
            has_liked: likes[video.uid] || false, // Сохраняем состояние лайка по uid
            has_disliked: dislikes[video.uid] || false // Сохраняем состояние дизлайка по uid
        }));

        try {
            const response = await fetch(update_url, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(updates),
            });

            if (!response.ok) {
                throw new Error('Failed to update videos');
            }

            const result = await response.json();
            console.log(result);
            fetchVideos(); // Обновляем список видео после успешного обновления
        } catch (error) {
            console.error('Error updating videos:', error);
        }
    };

    // Функция для обработки лайка видео
    const handleLike = (uid) => {
        setLikes(prevLikes => ({
            ...prevLikes,
            [uid]: !prevLikes[uid], // Переключаем лайк по uid
        }));
        setDislikes(prevDislikes => ({
            ...prevDislikes,
            [uid]: false, // Сбрасываем дизлайк
        }));
    };

    // Функция для обработки дизлайка видео
    const handleDislike = (uid) => {
        setDislikes(prevDislikes => ({
            ...prevDislikes,
            [uid]: !prevDislikes[uid], // Переключаем дизлайк по uid
        }));
        setLikes(prevLikes => ({
            ...prevLikes,
            [uid]: false, // Сбрасываем лайк
        }));
    };

    return (
        <div className="video-page">
            <div className="video-list">
                {videos.map((video) => (
                    <div
                        className="video-item"
                        key={video.uid} // Используем uid в качестве ключа
                        onMouseEnter={() => setHoveredVideo(video)}
                        onMouseLeave={() => setHoveredVideo(null)}
                    >
                        <div className="video-title">{video.title}</div>
                        <div className="video-controls">
                            <button
                                className={`reaction-btn ${likes[video.uid] ? 'active' : ''}`} // Проверяем по uid
                                onClick={() => handleLike(video.uid)}
                            >
                                •ᴗ•
                            </button>
                            <button
                                className={`reaction-btn ${dislikes[video.uid] ? 'active' : ''}`} // Проверяем по uid
                                onClick={() => handleDislike(video.uid)}
                            >
                                ˙◠˙
                            </button>
                        </div>
                        {hoveredVideo && hoveredVideo.uid === video.uid && ( // Сравниваем по uid
                            <div className="tooltip">
                                <div className="video-category">
                                    <strong>Категория:</strong> {video.category_name}
                                </div>
                                <div className="video-date">
                                    <strong>Дата:</strong> {video.created_at}
                                </div>
                            </div>
                        )}
                        <div className="current-date">{video.created_at}</div>
                    </div>
                ))}
            </div>
            <div className="refresh-recommendations">
                <button className="refresh-btn" onClick={handleRefresh}>Обновить рекомендации</button>
            </div>
        </div>
    );
};

export default VideoPage;
