import React, { useState, useEffect } from 'react';
import axios from 'axios'; // Импортируем axios
import './VideoPage.css';

const VideoPage = () => {
    const [videos, setVideos] = useState([]);
    const [hoveredVideo, setHoveredVideo] = useState(null);
    const [likes, setLikes] = useState({});
    const [dislikes, setDislikes] = useState({});
    const date = '2024-09-28';

    // Функция для получения данных с сервера
    const fetchVideos = async () => {
        try {
            const response = await axios.get('http://localhost:8000/api/videos/');
            setVideos(response.data); // Предполагается, что сервер возвращает массив видео
        } catch (error) {
            console.error('Ошибка при получении данных:', error);
        }
    };

    useEffect(() => {
        fetchVideos(); // Вызываем функцию получения данных при монтировании компонента
    }, []);

    const handleLike = (id) => {
        setLikes(prevLikes => ({
            ...prevLikes,
            [id]: prevLikes[id] ? false : true
        }));
        setDislikes(prevDislikes => ({
            ...prevDislikes,
            [id]: false
        }));
    };

    const handleDislike = (id) => {
        setDislikes(prevDislikes => ({
            ...prevDislikes,
            [id]: prevDislikes[id] ? false : true
        }));
        setLikes(prevLikes => ({
            ...prevLikes,
            [id]: false
        }));
    };

    return (
        <div className="video-page">
            <div className="video-list">
                {videos.map((video) => (
                    <div
                        className="video-item"
                        key={video.id}
                        onMouseEnter={() => setHoveredVideo(video)}
                        onMouseLeave={() => setHoveredVideo(null)}
                    >
                        <div className="video-title">{video.title}</div>
                        <div className="video-controls">
                            <button className={`reaction-btn ${likes[video.id] ? 'active' : ''}`} onClick={() => handleLike(video.id)}>
                                •ᴗ•
                            </button>
                            <button className={`reaction-btn ${dislikes[video.id] ? 'noactive' : ''}`} onClick={() => handleDislike(video.id)}>
                                ˙◠˙
                            </button>
                        </div>
                        {hoveredVideo && hoveredVideo.id === video.id && (
                            <div className="tooltip">
                                <div className="video-category">
                                    <strong>Категория:</strong> {video.category}
                                </div>
                            </div>
                        )}
                        <div className="current-date">{date}</div>
                    </div>
                ))}
            </div>
            <div className="refresh-recommendations">
                <button className="refresh-btn" onClick={fetchVideos}>Обновить рекомендации</button>
            </div>
        </div>
    );
};

export default VideoPage;
