import React, { useState, useEffect } from 'react';
import './VideoPage.css';
import logo from './logo.jpg';

const VideoPage = () => {
    const [loading, setLoading] = useState(true);
    const [videos, setVideos] = useState([]);
    const [hoveredVideo, setHoveredVideo] = useState(null);
    const [likes, setLikes] = useState({});
    const [dislikes, setDislikes] = useState({});
    const [refreshing, setRefreshing] = useState(false);

    // Обработка события прокрутки
    useEffect(() => {
        const handleWheel = (event) => {
            if (event.ctrlKey) {
                event.preventDefault();
            }
        };

        window.addEventListener('wheel', handleWheel, { passive: false });

        return () => {
            window.removeEventListener('wheel', handleWheel);
        };
    }, []);

    // Функция получения видео с сервера
    const fetchVideos = async (retryCount = 0) => {
        const apps_url = 'http://localhost:8000/api/videos/';
        setLoading(true);
        try {
            const response = await fetch(apps_url);
            if (!response.ok) {
                throw new Error('Network response was not ok ' + response.statusText);
            }
            const data = await response.json();
            console.log('Videos received from server:', data);
            setVideos(data);
        } catch (error) {
            if (retryCount < 3) {
                setTimeout(() => {
                    fetchVideos(retryCount + 1);
                }, 2000);
            } else {
                console.error('Error fetching videos:', error);
            }
        } finally {
            setLoading(false);
        }
    };

    // Функция получения рекомендаций
    const fetchRecommendations = async () => {
        const recommendation_url = 'http://localhost:8000/api/recommendations/';
        try {
            const response = await fetch(recommendation_url);
            if (!response.ok) {
                throw new Error('Failed to fetch recommendations: ' + response.statusText);
            }
            const recommendations = await response.json();
            console.log('Recommendations received from server:', recommendations);
            setVideos(recommendations); // Обновляем видео с новыми рекомендациями
        } catch (error) {
            console.error('Error fetching recommendations:', error);
        }
    };

    // Используем useEffect для загрузки видео один раз при загрузке компонента
    useEffect(() => {
        fetchVideos();
    }, []);

    // Функция для обновления лайков и дизлайков на сервере и получения обновленных видео
    const handleRefresh = async () => {
        setRefreshing(true);
        const update_url = 'http://localhost:8000/videos/update_likes_dislikes/';

        const updates = videos.map(video => ({
            uid: video.uid, 
            has_liked: likes[video.uid] || false,
            has_disliked: dislikes[video.uid] || false
        }));

        try {
            const response = await fetch(update_url, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(updates),
            });

            if (!response.ok) {
                throw new Error('Failed to update videos');
            }

            await fetchRecommendations(); // Получаем новые рекомендации после обновления лайков и дизлайков
        } catch (error) {
            console.error('Error updating videos:', error);
        } finally {
            setRefreshing(false);
        }
    };

    // Обработчики лайков и дизлайков
    const handleLike = (uid) => {
        setLikes(prevLikes => ({
            ...prevLikes,
            [uid]: !prevLikes[uid],
        }));
        setDislikes(prevDislikes => ({
            ...prevDislikes,
            [uid]: false,
        }));
    };

    const handleDislike = (uid) => {
        setDislikes(prevDislikes => ({
            ...prevDislikes,
            [uid]: !prevDislikes[uid],
        }));
        setLikes(prevLikes => ({
            ...prevLikes,
            [uid]: false,
        }));
    };

    return (
        <div className="video-page">
            <img src={logo} alt="Логотип" className="logo" />
            {loading ? (
                <div className="loading-indicator">Загрузка данных...</div>
            ) : (
                <div className="video-list">
                    {videos.map((video) => (
                        <div
                            className="video-item"
                            key={video.uid}
                            onMouseEnter={() => setHoveredVideo(video)}
                            onMouseLeave={() => setHoveredVideo(null)}
                        >
                            <div className="video-title">{video.title}</div>
                            <div className="video-controls">
                                <button
                                    className={`reaction-btn ${likes[video.uid] ? 'active' : ''}`}
                                    onClick={() => handleLike(video.uid)}
                                >
                                    •ᴗ•
                                </button>
                                <button
                                    className={`reaction-btn ${dislikes[video.uid] ? 'noactive' : ''}`}
                                    onClick={() => handleDislike(video.uid)}
                                >
                                    ˙◠˙
                                </button>
                            </div>
                            {hoveredVideo && hoveredVideo.uid === video.uid && (
                                <div className="tooltip">
                                    <div className="video-category">
                                        <strong>Категория:</strong> {video.category_name}
                                    </div>
                                    <div className="video-date">
                                        <strong>Дата:</strong> {video.created_at}
                                    </div>
                                </div>
                            )}
                            <div className="current-date">{video.created_at}</div>
                        </div>
                    ))}
                </div>
            )}
            <div className="refresh-recommendations">
                <button 
                    className="refresh-btn" 
                    onClick={handleRefresh} 
                    disabled={refreshing} // Disable button while refreshing
                >
                    {refreshing ? 'Обновление...' : 'Обновить рекомендации'}
                </button>
            </div>
        </div>
    );
};

export default VideoPage;
